import { FlatList, Image, StatusBar, StyleSheet, Text, TextInput, View } from "react-native";
import Categories from "./Categories";
import { useEffect, useState } from "react";
import Event from "./Event";
import axios from 'axios'
import FixedBottomView from "../FixedBottomView";

export default function Content() {

    const [commonCategories] = useState([
        {
            name: "All",
            icon: "star",
        },
        {
            name: "Science",
            icon: "atom",
        },
        {
            name: "Culture",
            icon: "language",
        },
        {
            name: "Sport",
            icon: "running",
        },
        {
            name: "Voluntary",
            icon: "hands-helping",
        },
    ]);
    const [term, setTerm] = useState(0);
    const [categorie, setCategorie] = useState("All");
    const [events, setEvents] = useState([]); // State to hold events data


    // GET DATA FROM DATABASE
    useEffect(() => {//For fetching Data
        const fetchData = async () => {
            try {
                //let categorie = "All";
                const response = await axios.get('http://192.168.1.38:5000/api/event/cat/' + categorie);
                //console.log(response.data);
                const fetchedEvents = response.data;
                setEvents(fetchedEvents);
                console.log(categorie)
            } catch (err) {
                console.log(err)
            }
        }
        fetchData();
    }, [categorie])
    return (

        <View style={styles.container}>

            <View >
                <Text style={styles.header1}>Get the most important</Text>
                <Text style={styles.header2}>activities!</Text>
            </View>

            <FlatList
                data={commonCategories}
                keyExtractor={(category) => category.name}
                renderItem={({ item, index }) => (
                    <Categories name={item.name} nameIcon={item.icon}
                        index={index}
                        active={term === index}
                        onPressCategoryItem={() => {
                            setTerm(index)
                            setCategorie(item.name)
                        }}
                    />
                )}
                horizontal
                showsHorizontalScrollIndicator={false}
            />

            <View>
                <Text style={styles.header3}>Activities</Text>
                <View style={{ height: 400 }}>
                    <FlatList
                        data={events}
                        keyExtractor={(category) => category._id}
                        renderItem={({ item, index }) => (
                            <Event id={item._id} title={item.title} image={{ uri: `${item.image}` }} />
                        )}
                        horizontal={false}
                        showsHorizontalScrollIndicator={false}
                        showsVerticalScrollIndicator={false}
                    />
                </View>
            </View>
            <View>
                <FixedBottomView />
            </View>

        </View>
    );
}

const styles = StyleSheet.create({
    container: {
        marginHorizontal: 25,
        marginVertical: 15
    },
    header1: {
        fontSize: 35,
        marginTop: 10,
    },
    header2: {
        fontSize: 40,
        fontWeight: "bold",
    },
    header3: {
        fontSize: 40,
        fontWeight: "bold",
        marginVertical: 15
    },
});
